//oldbuilding
function showGroundFloor() {
    var elements = document.getElementsByClassName("dropdown-btn");
    for (var i = 0; i < elements.length; i++) {
        elements[i].style.display = "block";
    }

    elements = document.getElementsByClassName("oldbldg");
    for (var i = 0; i < elements.length; i++) {
        elements[i].style.display = "block";
    }

    var floors = ["oldgroundfloor", "oldmez", "secondold", "thirdold", "fourthold", "uclmmap", "fifthold", "rdold"];
    for (var i = 0; i < floors.length; i++) {
        var element = document.getElementById(floors[i]);
        if (floors[i] === "oldgroundfloor") {
            element.style.display = "block";
        } else {
            element.style.display = "none";
        }
    }
}

